$("path.cls-1").click(function(e) {

  document.getElementById('loader').classList.add('on');
  setTimeout(function () {
    document.getElementById('loader').classList.add('go');
  }, 100);

  $("#app").empty();

  var frame = document.createElement('div');
  frame.id = 'page';
  frame.classList.add('container');

  document.getElementById(e.target.id).classList.add('map-select');

  $("#app").append(frame);

  var num = '';
  for (var i = 0; i < data.length; i++) {
    if (data[i].Key == e.target.id) {
      num = i;
      $("#page").append('<h2>' + data[num].Name + '</h2>');
      break;
    }
  }

  if (data[num].Desc) {
    for (var i = 0; i < data[num].Desc.length; i++) {
      $("#page").append('<div class="block">' + data[num].Desc[i] + '</div>');
    }
  } else{
   $("#page").append('<h4 class="my-4">Информация еще не добавлена</h4><p>Обратитесь в нашу поддержку для совместрого сотруднечества</p>');
  }

  $("#page").append('<h5 class="text-center">А так же другие...</h5>');

  setTimeout(function () {
    document.getElementById('loader').classList.remove('go');
    setTimeout(function () {
      document.getElementById('loader').classList.remove('on');
    }, 100);
  }, 300);

  $('img').click(function(e) {
    $('#modal-img').attr('src', e.target.src);
    $('#myModal').modal('show');
  });

});

window.onload = function () {

  $('.nav-items').click( function (e) {
    var data = e.target.getAttribute("data-name");
    switch (data) {
      case '/':
        Home();
        break;
    }
  });

  var count = 0;
  for (var i = 0; i < data.length; i++) {
    if (data[i].Desc) {
      for (var j = 0; j < data[i].Desc.length; j++) {
        count++;
      }
    }
  }

  var countBar = document.querySelectorAll("[data-name='count']")[0];
  countBar.innerHTML = 'На нашем сайте уже <b>' + count + '</b> храмов!';

  document.getElementById('footer').classList.add('text-center', 'p-5');
  $("#footer").html('Erilov Nikita');

};
